import React from 'react';
import './App.css';

let shoppingCartItems = [
  {
    item: 'War and Peace',
    image: 'https://images4.penguinrandomhouse.com/cover/9781400079988',
    description: 'War and Peace broadly focuses on Napoleon’s invasion of Russia in 1812 and follows three of the most well-known characters in literature',
    price: 100 
  },
  {
    item: 'Cat',
    image: 'http://bit.ly/34l1k66',
    description: 'You cannot live without your fav babe',
    price: 200
  },
  {
    item: 'Magic IG Filter Camera',
    image: "http://bit.ly/2NcGDDV",
    description: "You, as a earth star IG influencer cannot live without but sorry now you have to take pics by yourself cuz you cannot bring your cameraman to Mars",
    price: 5000
  }
]


function App() {
  return (
    <div className="App">
      <h1>Your Shopping Cart</h1>
     <SearchForm />
     <ShoppingCart />
    </div>
  );
}

function ShoppingCart() {
  return(
    <div className="cart">
      {shoppingCartItems.map(Product)} 
      {shoppingCartItems.map(AddDollarSign)}
    </div>
  );
}

function AddDollarSign(needDollar) {
  return (
    <p>{"$" + needDollar.price}</p>
  )
}

function Product(imageGood) {
  return (
    <div className="overallitem">
    <div className="product">
      <img src={imageGood.image} width="100" alt="hhh"/>
    </div>
    <div className="productname">
      <h1>{imageGood.item}</h1>
    </div>
    <div className="descript">
      <p>{imageGood.description}</p>
    </div>
    <div className="price">
      <p>{imageGood.price}</p>
    </div>
    </div>
  );
}

function SearchForm() {
  return(
    <form>
      <label>
        Search:
        <input type="text" name="name" />
      </label>
      <input type="submit" value="Submit" />
    </form>
  );
}

export default App;
